// Soil type card with expandable details

// Define the type for soilType prop
interface SoilType {
  type: string;
  image: string;
  crops: string[];
  fertilizers: string[];
  seasons: string[];
}

// Define the props for the SoilTypeCard component
interface SoilTypeCardProps {
  soilType: SoilType;
  isExpanded: boolean;
  onClick: () => void;
}

const SoilTypeCard: React.FC<SoilTypeCardProps> = ({ soilType, isExpanded, onClick }) => (
  <div className={`soil-card ${isExpanded ? 'expanded' : ''}`} onClick={onClick}>
    <h3>{soilType.type}</h3>
    <img src={soilType.image} alt={soilType.type} />
    {isExpanded && (
      <div className="soil-detail">
        <p><strong>Crops:</strong> {soilType.crops.join(', ')}</p>
        <p><strong>Fertilizers:</strong> {soilType.fertilizers.join(', ')}</p>
        <p><strong>Best Seasons:</strong> {soilType.seasons.join(', ')}</p>
        <img src={soilType.image} alt={`${soilType.type} Image`} />
      </div>
    )}
  </div>
);

// Main Crop Guide component
const CropGuide = () => {
  const [expandedSoilIndex, setExpandedSoilIndex] = useState<number | null>(null);

  const toggleExpansion = (index: number) => {
    setExpandedSoilIndex(expandedSoilIndex === index ? null : index);
  };

  return (
    <div className="crop-guide">
      <h1>Crop Guide by Soil Type</h1>
      <div className="soil-grid">
        {soilData.map((soil, index) => (
          <SoilTypeCard
            key={index}
            soilType={soil}
            isExpanded={expandedSoilIndex === index}
            onClick={() => toggleExpansion(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default CropGuide;
